function displayTans(){for(var e=document.getElementsByClassName("w-select"),t=document.getElementsByClassName("w-transparent")[0],n=0;n<e.length;n++)e[n].addEventListener("click",function(){t.style.display="block"});t.addEventListener("click",function(e){"w-transparent"==e.target.className&&(t.style.display="none")})}function judeg(){for(var e=document.getElementsByClassName("w-bagGeneral"),t=0;t<e.length;t++)"0"==e[t].getAttribute("data-isGet")&&(e[t].getElementsByTagName("button")[0].style.display="block",e[t].getElementsByTagName("img")[1].style.display="none")}window.onload=function(){displayTans(),judeg()};